document.addEventListener('DOMContentLoaded', () => {
    console.log('Document is ready');

    const movies = [
        {
            title: "The Matrix",
            description: "A computer hacker learns about the true nature of reality and his role in the war against its controllers.",
            image: "data:image/jpeg;base64,"
        },
        {
            title: "Inception",
            description: "A thief who enters the dreams of others to steal secrets must perform the impossible task of planting an idea.",
            image: "data:image/jpeg;base64,"
        },
        {
            title: "Interstellar",
            description: "A team of explorers travel through a wormhole in space in an attempt to ensure humanity's survival.",
            image: "data:image/jpeg;base64,"
        },
        {
            title: "The Dark Knight",
            description: "Batman faces the Joker, a criminal mastermind who wants to plunge Gotham City into anarchy.",
            image: "data:image/jpeg;base64,"
        },
        {
            title: "Fight Club",
            description: "An insomniac office worker and a soap salesman form an underground fight club.",
            image: "data:image/jpeg;base64,"
        },
        {
            title: "Pulp Fiction",
            description: "The lives of two mob hitmen, a boxer, a gangster's wife, and a pair of diner bandits intertwine.",
            image: "data:image/jpeg;base64,"
        },
        {
            title: "Forrest Gump",
            description: "The presidencies of Kennedy and Johnson, the Vietnam War, and other historical events unfold through the perspective of an Alabama man with a low IQ.",
            image: "data:image/jpeg;base64,"
        },
        {
            title: "The Shawshank Redemption",
            description: "Two imprisoned men bond over a number of years, finding solace and eventual redemption through acts of common decency.",
            image: "data:image/jpeg;base64,"
        },
    ];

    const movieGrid = document.getElementById('movie-grid');

    const createMovieCard = (movie) => {
        const card = document.createElement('div');
        card.className = 'bg-gray-800 rounded overflow-hidden shadow-lg';

        const img = document.createElement('img');
        img.src = movie.image;
        img.alt = `${movie.title} Poster`;
        img.className = 'w-full';

        const cardBody = document.createElement('div');
        cardBody.className = 'p-4';

        const cardTitle = document.createElement('h3');
        cardTitle.className = 'text-xl font-semibold mb-2';
        cardTitle.textContent = movie.title;

        const cardDescription = document.createElement('p');
        cardDescription.className = 'text-gray-400';
        cardDescription.textContent = movie.description;

        cardBody.appendChild(cardTitle);
        cardBody.appendChild(cardDescription);
        card.appendChild(img);
        card.appendChild(cardBody);

        return card;
    };

    movies.forEach(movie => {
        const movieCard = createMovieCard(movie);
        movieGrid.appendChild(movieCard);
    });
});
